visible.script:
'use strict';

/** Length of the day in minutes. */
let day_length = 5;
/** The currently visible background layer .*/
let activeBG = undefined;
/** The currently visible foreground layer. */
let activeFG = undefined;
/** The currently visible foreground lamps layer. */
let activeLamps = undefined;
/** The 'sun height' (1-12) value from the previous frame. */
let prevSunHeight = 0;

/** Register assets for publishing */
for (let i = 1; i <= 12; ++i) {
	engine.registerAsset(`models/BG${i}.json`);
	engine.registerAsset(`models/FG${i}.json`);
	engine.registerAsset(`models/FGLamps${i}.json`);
}

/** Linearly interpolates between two values based on the third value */
function lerp(a, b, t) { return a + (b-a)*t; }




export function init() {
	// Precache background and foreground layers
	for (let i = 1; i <= 12; ++i) {
		loadBG(i).visible = false;
		loadFG(i).visible = false;
		loadLamps(i).visible = false;
	}
}



export function update() {
	let sunHeight = getSunHeight();
	if (sunHeight != prevSunHeight)
	{
		if (activeBG)
			activeBG.visible = false;
		if (activeFG)
			activeFG.visible = false;
		if (activeLamps)
			activeLamps.visible = false;

		activeBG = thisScene.getLayer(`BG${sunHeight}`) || loadBG(sunHeight);
		thisScene.sortLayer(activeBG, thisScene.getLayerIndex(thisLayer) + 1);

		activeFG = thisScene.getLayer(`FG${sunHeight}`) || loadFG(sunHeight);
		thisScene.sortLayer(activeFG, thisScene.getLayerIndex(activeBG) + 1);

		activeLamps = thisScene.getLayer(`FGLamps${sunHeight}`) || loadLamps(sunHeight);
		thisScene.sortLayer(activeLamps, thisScene.getLayerIndex("Pixel step (512x256 White)") - 1);

		activeBG.visible = true;
		activeFG.visible = true;
		activeLamps.visible = true;
	}

	prevSunHeight = sunHeight;
}



/**
 * @param {Object} userProperties
 */
export function applyUserProperties(userProperties) {
	if (userProperties.day_length != undefined)
		day_length = userProperties.day_length;
}



/**
 * Returns a number between 1-12, where 1 means the sun is completely down, and 12 means it is day time.
 */
function getSunHeight() {
	let timeOfDay = ((engine.timeOfDay * 1440) % day_length) / day_length;
	let hour = Math.abs((timeOfDay - 0.5) * 24);
	if (hour < 6)
		return 12;
	else if (hour > 8)
		return 1;
	else
		return Math.floor(lerp(12, 1, (hour - 6) * 0.5));
}



function loadBG(sunHeight) {
	return thisScene.createLayer({
		name: `BG${sunHeight}`,
		image: `models/BG${sunHeight}.json`,
		origin: `256 128 0`,
		size: `512.000 256.000`,
		effects: 
		[
			{
				"file" : "effects/scroll/effect.json",
				"passes" : 
				[
					{
						"constantshadervalues" : 
						{
							"speedx" : 
							{
								"user" : "bg_speed"
							},
							"speedy" : 0
						}
					}
				],
				"visible" : true
			},
			{
				"file" : "effects/Perspective_Mask/effect.json",
				"name" : "",
				"passes" : 
				[
					{
						"constantshadervalues" : 
						{
							"scale" : 
							{
								"script" : "'use strict';\n\n/**\n * @param {Object} userProperties\n */\nexport function applyUserProperties(userProperties) {\n\tif (userProperties.perspective !== undefined)\n\t\tthisObject.scale = userProperties.perspective * 1.0;\n}\n",
								"value" : 1
							}
						},
						"textures" : [ null, "BGDepth" ]
					}
				]
			}
		]
	});
}



function loadFG(sunHeight) {
	return thisScene.createLayer({
		name: `FG${sunHeight}`,
		image: `models/FG${sunHeight}.json`,
		origin: `256 128 0`,
		size: `512.000 256.000`,
		effects: 
		[
			{
				"file" : "effects/scroll/effect.json",
				"passes" : 
				[
					{
						"constantshadervalues" : 
						{
							"speedx" : 
							{
								"user" : "fg_speed"
							},
							"speedy" : 0
						}
					}
				]
			},
			{
				"file" : "effects/Perspective_Mask/effect.json",
				"passes" : 
				[
					{
						"constantshadervalues" : 
						{
							"scale" : 
							{
								"script" : "'use strict';\n\n/**\n * @param {Object} userProperties\n */\nexport function applyUserProperties(userProperties) {\n\tif (userProperties.perspective !== undefined)\n\t\tthisObject.scale = userProperties.perspective * 3.0;\n}\n"
							}
						},
						"textures" : [ null, "FGDepth" ]
					}
				]
			}
		]
	});
}



function loadLamps(sunHeight) {
	return thisScene.createLayer({
		name: `FGLamps${sunHeight}`,
		image: `models/FGLamps${sunHeight}.json`,
		origin: `256 128 0`,
		size: `512.000 256.000`,
		effects: 
		[
			{
				"file" : "effects/scroll/effect.json",
				"passes" : 
				[
					{
						"constantshadervalues" : 
						{
							"repeat" : "1 1",
							"speedx" : 
							{
								"user" : "fg_speed"
							},
							"speedy" : 0
						}
					}
				]
			}
		]
	});
}

origin.script:
'use strict';

// let position = 0.25;


/**
 * @param {Object} userProperties
 */
export function applyUserProperties(userProperties) {
	if (userProperties.megaman_position !== undefined)
	{
		// position = userProperties.megaman_position;

		let newPos = new Vec3(engine.canvasSize.x * userProperties.megaman_position, thisLayer.origin.y, thisLayer.origin.z);

		thisLayer.origin = newPos;
		thisScene.getLayer("MMX1HelmetRun").origin = newPos;
		thisScene.getLayer("MMX2HelmetRun").origin = newPos;
		thisScene.getLayer("MMX1ArmsRun").origin = newPos;
		thisScene.getLayer("MMX1BootsRun").origin = newPos;
	}
	if (userProperties.show_megaman !== undefined)
	{
		let alpha = new Vec3(userProperties.show_megaman ? 1 : 0);
		thisLayer.scale = alpha;
		thisScene.getLayer("MMX1HelmetRun").scale = alpha;
		thisScene.getLayer("MMX2HelmetRun").scale = alpha;
		thisScene.getLayer("MMX1ArmsRun").scale = alpha;
		thisScene.getLayer("MMX1BootsRun").scale = alpha;
	}
}

